let previousSensorId = global.get("previousSensorId");

// Unsubscribe if there is a previously saved sensor id
if (previousSensorId){
    msg.payload = String("VL/MG/") + String("S") + global.get("previousSensorId") + String("/");
    msg.action = "unsubscribe";
    return msg;
}

// Set the global variable to be used during unsubscription
global.set("previousSensorId", msg.payload.id);

msg.payload = String("VL/MG/") + String("S") + msg.payload.id + String("/");
msg.action = "subscribe";
return msg;
